import React from "react";
import "../CSS/File.css";
import { Link } from "react-router-dom";
import {useRef} from 'react';
import Tweet from "./Tweet";

function File() {
    const [tweetList] = React.useState([]);
    const tweet = useRef(null);

    const sendTweet = event => {
        event.preventDefault();
        tweetList.push(tweet.current.value);
        event.target.reset();
    }

    return <div className="File">
        <div className="welcome">
            <Link to="/accueil" className="link"><p>Accueil</p></Link>
        </div>
        <div className="textBox">
            <form onSubmit={sendTweet}>
                <input placeholder="Quoi de neuf ?" ref={tweet}></input>
                <button type="submit" className="button" >Tweet</button>
            </form>

        </div>
        <ul className="tweetList">
            {tweetList.map((e) => {
                <Tweet content={e}></Tweet>
            })}
        </ul>
    </div>;
}
export default File;
