import React, { useState } from "react";
import '../CSS/Accueil.css';
import { useLocation } from "react-router-dom";
import Tache from "./Tache";
import File from "./File";
import Trend from "./Trends";

function Accueil() {
    const location = useLocation();
    const [info, setInfo] = useState('');

    function sendInfo(info) {
        setInfo(info);
    }

    return <div className="Accueil">
        <Tache sendInfo={sendInfo}></Tache>
        <File email={location.state.email} firstName={location.state.firstName} lastName={location.state.lastName} _id={location.state._id}></File>
        <Trend info={info}></Trend>
    </div>
} 
export default Accueil;
