import React from "react";
import "../CSS/File.css";
import { Link } from "react-router-dom";
import {useRef} from 'react';
import Tweet from "./Tweet";
import axios from 'axios';
import Cookies from 'js-cookie';

function File({email, firstName, lastName, _id}) {
    const [tweetList, setTweetList] = React.useState([]);
    const tweet = useRef(null);
    const token = Cookies.get('token');

    React.useEffect(() => {
        fetch("/tweets/getTweets")
          .then((res) => res.json())
          .then((data) => {
            // console.log(data);
            const tweets = [];
            data.forEach((element) => {
                element.tweets.forEach((e) => {
                    const t = {
                        _id: e._id,
                        content: e.content,
                        firstName: element.author.firstName,
                        lastName: element.author.lastName
                    }
                    tweets.push(t);
                });
            });
            setTweetList(tweets);
          });
      }, []);

    const sendTweet = event => {
        event.preventDefault();
        console.log(tweetList);
        axios
            .post('/tweets/postTweet', {email: email, content: tweet.current.value, _id: _id}, { headers: {authoization: token}})
            .then((response) => {
                console.log(response.data);
                setTweetList([...tweetList, response.data]);
            })
            .catch(err => {
                console.error(err);
              });
        event.target.reset();
    }

    return <div className="File">
        <div className="welcome">
            <Link to="{{
                pathname: '/accueil',
                state: {
                    email: email,
                    firstName: firstName,
                    lastName: lastName
                }
            }}" className="link"><p>Accueil</p></Link>
        </div>
        <div className="top">
            <div className="profil">
                <p>{firstName}</p>
                <p>{lastName}</p>
            </div>
            <div className="textBox">
                <form onSubmit={sendTweet}>
                    <input placeholder="Quoi de neuf ?" ref={tweet}></input>
                    <button type="submit" className="button" >Tweeter</button>
                </form>
            </div>
        </div>
        <div className="tweetList">
            {tweetList.map((e) => (
                <Tweet key={e._id} content={e.content} firstName={e.firstName} lastName={e.lastName}></Tweet>
            ))}
        </div>
    </div>;
}
export default File;
