import React from "react";
import "../CSS/File.css";
import { Link } from "react-router-dom";
import {useRef} from 'react';
import Tweet from "./Tweet";
import axios from 'axios';
import Cookies from 'js-cookie';

function File({email, firstName, lastName, _id}) {
    const [tweetList, setTweetList] = React.useState([]);
    const tweet = useRef(null);
    const token = Cookies.get('token');

    function timeConverter(time) {
        const date = Date.now() - (time);
        const min = Math.floor(date/(60*1000));
        const hour = Math.floor(date/(60*60*1000));
        const day = Math.floor(date/(24*60*60*1000));
        if(date < (60*1000)) {
            return 'Just now';
        } else {
            if(!hour) {
                return min+' min';
            } else {
                if(!day) {
                    return hour+' h '+(min-hour*60)+' min';
                } else {
                    return day+' j '+(hour-day*24)+' h '+(min-hour*60)+' min';
                }
            }
        }
    }

    React.useEffect(() => {
        fetch("/tweets/getTweets")
          .then((res) => res.json())
          .then((data) => {
            const tweets = [];
            data.forEach((element) => {
                element.tweets.forEach((e) => {
                    const t = {
                        _id: e._id,
                        content: e.content,
                        firstName: element.author.firstName,
                        lastName: element.author.lastName,
                        date: timeConverter(e.date),
                        userID: element.author._id
                    }
                    tweets.push(t);
                });
            });
            setTweetList(tweets);
          });
      }, []);

    const sendTweet = event => {
        event.preventDefault();
        tweet.current.value !== '' &&
        axios
            .post('/tweets/postTweet', {email: email, content: tweet.current.value, date: Date.now(), _id: _id}, { headers: {authorization: 'Bearer ' + token}})
            .then((response) => {
                response.data.date = timeConverter(response.data.date);
                setTweetList([...tweetList, response.data]);
            })
            .catch(err => {
                console.error(err);
              });
        event.target.reset();
    }

    function refreshTweetList(tweetID) {
        // const index = tweetList.findIndex(tweetList.find(e => e._id===tweetID));
        // if (index !== -1) {
        //     tweetList.splice(index, 1);
        // }
        setTweetList(tweetList.filter(e => e['_id']!==tweetID));
    }

    return <div className="File">
        <div className="welcome link" onClick={() => window.location.reload()}>
            <p>Accueil</p>
        </div>
        <div className="top">
            <div className="profil">
                <p>{firstName}</p>
                <p>{lastName}</p>
            </div>
            <div className="textBox">
                <form onSubmit={sendTweet}>
                    <input placeholder="Quoi de neuf ?" ref={tweet}></input>
                    <button type="submit" className="button" >Tweeter</button>
                </form>
            </div>
        </div>
        <div className="tweetList">
            {tweetList.map((e) => (
                <Tweet key={e._id} content={e.content} firstName={e.firstName} lastName={e.lastName} 
                _id={e._id} userID={e.userID} date={e.date} refreshTweetList={refreshTweetList}></Tweet>
            ))}
        </div>
    </div>;
}
export default File;
