import React from "react";
import "../CSS/Profil.css";
import Cookies from 'js-cookie';
import Tweet from "./Tweet";
import axios from 'axios';

function Profil({email, firstName, lastName, _id}) {
    const [tweetList, setTweetList] = React.useState([]);
    const token = Cookies.get('token');

    React.useEffect(() => {
        axios.get(`/tweets/getUserTweets/${_id}`, { headers: {authorization: 'Bearer ' + token}})
            .then((response) => {
                console.log(response.data)
                if(!response.data.message) {
                    const tweets = [];
                        response.data.tweets.forEach((e) => {
                            const t = {
                                _id: e._id,
                                content: e.content,
                                firstName: response.data.author.firstName,
                                lastName: response.data.author.lastName
                            }
                            tweets.push(t);
                        });
                    setTweetList(tweets);
                }
            });
      }, []);

    return <div className="Profil">
        <div className="top">
            <div className="profil">
                <p>{firstName}</p>
                <p>{lastName}</p>
            </div>
        </div>
        <div className="tweetList">
            {tweetList.map((e) => (
                <Tweet key={e._id} content={e.content} firstName={e.firstName} lastName={e.lastName} _id={e._id} userID={_id}></Tweet>
            ))}
        </div>
    </div>;
}
export default Profil;
