import React, { useState } from "react";
import '../CSS/Accueil.css';
import { useLocation } from "react-router-dom";
import { BrowserRouter, Link, Route, Routes } from 'react-router-dom';
import Tache from "./Tache";
import File from "./File";
import Trend from "./Trends";
import Profil from "./Profil";

function Accueil() {
    const location = useLocation();
    const [element, setElement] = useState(<File email={location.state.email} firstName={location.state.firstName} lastName={location.state.lastName} _id={location.state._id}></File>);

    function sendInfo(info) {
        // setElement(info);
        switch (info) {
            case 'Home':
                setElement(
                <File email={location.state.email} firstName={location.state.firstName} lastName={location.state.lastName} _id={location.state._id}></File>
                );
                break;
            case 'Profil':
                setElement(<Profil email={location.state.email} firstName={location.state.firstName} lastName={location.state.lastName} _id={location.state._id}></Profil>);
                break;
            default :
                setElement(<File email={location.state.email} firstName={location.state.firstName} lastName={location.state.lastName} _id={location.state._id}></File>);
                break;
        }
    }

    return <div className="Accueil">
        <Tache sendInfo={sendInfo}></Tache>
        <BrowserRouter>
            <Routes>
                <Route path="/accueil">
                    <File email={location.state.email} firstName={location.state.firstName} lastName={location.state.lastName} _id={location.state._id}></File>
                </Route>
                <Route path="/profil">
                    <Profil email={location.state.email} firstName={location.state.firstName} lastName={location.state.lastName} _id={location.state._id}></Profil>
                </Route>
            </Routes>
        </BrowserRouter>
        <Trend></Trend>
    </div>
} 
export default Accueil;
