import React from "react";
import "../CSS/File.css";
import { Link } from "react-router-dom";
import {useRef} from 'react';
import Tweet from "./Tweet";
import axios from 'axios';
import Cookies from 'js-cookie';

function File({email, firstName, lastName, _id}) {
    const [tweetList, setTweetList] = React.useState([]);
    const tweet = useRef(null);
    const token = Cookies.get('token');

    function timeConverter(time) {
        const date = new Date((Math.floor((Date.now() - time)/1000)) * 1000).toISOString().slice(11, 19);
        if(date.getMinutes() === 0) {
            return 'Just now';
        } else {
            if(date.getHours() === 0) {
                return date.getMinutes()+' min';
            } else {
                return date.getHours()+' h '+date.getMinutes()+' min';
            }
        }
        return 'ok'
    }

    React.useEffect(() => {
        fetch("/tweets/getTweets")
          .then((res) => res.json())
          .then((data) => {
            console.log(data);
            const tweets = [];
            data.forEach((element) => {
                element.tweets.forEach((e) => {
                    const t = {
                        _id: e._id,
                        content: e.content,
                        firstName: element.author.firstName,
                        lastName: element.author.lastName,
                        date: timeConverter(e.date)
                    }
                    tweets.push(t);
                });
            });
            setTweetList(tweets);
          });
      }, []);

    const sendTweet = event => {
        event.preventDefault();
        axios
            .post('/tweets/postTweet', {email: email, content: tweet.current.value, date: Date.now(), _id: _id}, { headers: {authorization: 'Bearer ' + token}})
            .then((response) => {
                console.log(response.data);
                setTweetList([...tweetList, response.data]);
            })
            .catch(err => {
                console.error(err);
              });
        event.target.reset();
    }

    return <div className="File">
        <div className="welcome link" onClick={() => window.location.reload()}>
            <p>Accueil</p>
        </div>
        <div className="top">
            <div className="profil">
                <p>{firstName}</p>
                <p>{lastName}</p>
            </div>
            <div className="textBox">
                <form onSubmit={sendTweet}>
                    <input placeholder="Quoi de neuf ?" ref={tweet}></input>
                    <button type="submit" className="button" >Tweeter</button>
                </form>
            </div>
        </div>
        <div className="tweetList">
            {tweetList.map((e) => (
                <Tweet key={e._id} content={e.content} firstName={e.firstName} lastName={e.lastName} _id={e._id} userID={_id} date={e.date}></Tweet>
            ))}
        </div>
    </div>;
}
export default File;
