// client/src/App.js

import React from "react";
import "./App.css";
import axios from 'axios';
import { useNavigate } from "react-router-dom";

function App() {
  const [data, setData] = React.useState(null);
  const [login, setLogin] = React.useState(false);
  const [signin, setSignin] = React.useState(false);
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');
  const [message, setMessage] = React.useState('');
  const [firstName, setFirstName] = React.useState('');
  const [lastName, setLastName]= React.useState('');
  let navigate = useNavigate();

  React.useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => setData(data.message));
  }, []);

  function setLS(bool) {
    setLogin(bool);
    setSignin(!bool);
  }

  function checkID() {
     axios
       .post('/checkID', {email: email, password: password})
       .then((response) => {
         console.log(response.data);
         if (response.data.status === 200) { 
           navigate('/accueil');
         }
         alert(response.data.message);
       })
       .catch(err => {
         console.error(err);
       });
    // fetch("/api")
    //   .then((res) => res.json())
    //   .then((data) => setData(data.message));
  }

  function signIn() {
    axios
      .post('/signIn', {firstName: firstName, lastName: lastName, email: email, password: password})
      .then((response) => {
        console.log("h");
        setMessage(response.data.message);
        console.log(response.data.message);
        setLS(true);
        alert(response.data.message);
      })  
      .catch(err => {
        console.error(err);
      });
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome !</h1>
        <p>{!data ? "Loading..." : data}</p>
        <div>
          <button type="button" className="button" onClick={() => setLS(true)}>Connexion</button>
          <button type="button" className="button" onClick={() => setLS(false)}>Inscription</button>
        </div>
        <div>
          {login && <div>
            <p>Email : <input type="email" onInput={e => setEmail(e.target.value)}></input></p>
            <p>Password : <input type="password" onInput={e => setPassword(e.target.value)}></input></p> 
            <button type="button" className="button" onClick={checkID}>Login</button>
            <div>{message}</div>
          </div>}
          {signin && <div>
            <p>First Name : <input type="email" onInput={e => setFirstName(e.target.value)}></input></p>
            <p>Last Name : <input type="email" onInput={e => setLastName(e.target.value)}></input></p>
            <p>Email : <input type="email" onInput={e => setEmail(e.target.value)}></input></p>
            <p>Password : <input type="password" onInput={e => setPassword(e.target.value)}></input></p> 
            <button type="button" className="button" onClick={signIn}>Sign In</button>
            </div>}
        </div>
      </header>
    </div>
  );
}

export default App;
