import React from "react";
import "../CSS/File.css";
import { Link } from "react-router-dom";
import {useRef} from 'react';
import Tweet from "./Tweet";
import axios from 'axios';

function File({email, firstName, lastName}) {
    const [tweetList, setTweetList] = React.useState([]);
    const tweet = useRef(null);

    React.useEffect(() => {
        fetch("/getTweets")
          .then((res) => res.json())
          .then((data) => {
              setTweetList(data);
              console.log(tweetList);
          });
      }, []);

    const sendTweet = event => {
        event.preventDefault();
        setTweetList([...tweetList, {text: tweet.current.value, id: tweetList.length+1}]);
        axios
            .post('/postTweet', {firstName: firstName, lastName: lastName, content: tweet.current.value, id: tweetList.length})
            .then((response) => {
                console.log(response.data.message);
            })
            .catch(err => {
                console.error(err);
              });
        event.target.reset();
    }

    return <div className="File">
        <div className="welcome">
            <Link to="/accueil" className="link"><p>Accueil</p></Link>
        </div>
        <div className="top">
            <div className="profil">
                <p>{firstName}</p>
                <p>{lastName}</p>
            </div>
            <div className="textBox">
                <form onSubmit={sendTweet}>
                    <input placeholder="Quoi de neuf ?" ref={tweet}></input>
                    <button type="submit" className="button" >Tweeter</button>
                </form>
            </div>
        </div>
        <div className="tweetList">
            {tweetList.map((e) => (
                <Tweet key={e.id} content={e.text} firstName={firstName} lastName={lastName}></Tweet>
            ))}
        </div>
    </div>;
}
export default File;
